import React, { useState } from 'react';
import UrlInput from './components/UrlInput';
import Results from './components/Results';
import { analyzeSEO } from './utils/seoAnalyzer';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [analyzedUrl, setAnalyzedUrl] = useState('');

  const handleAnalyze = async (url) => {
    setIsLoading(true);
    setError(null);
    setResults(null);
    setAnalyzedUrl(url);

    try {
      // Use a CORS proxy to fetch the HTML content
      const proxyUrl = `https://api.allorigins.win/get?url=${encodeURIComponent(url)}`;
      const response = await fetch(proxyUrl);

      if (!response.ok) {
        throw new Error('Failed to fetch website content');
      }

      const data = await response.json();

      if (!data.contents) {
        throw new Error('No content received from the website');
      }

      // Analyze the HTML content
      const analysisResults = analyzeSEO(data.contents);
      setResults(analysisResults);

    } catch (err) {
      console.error('Analysis error:', err);
      setError(err.message || 'An error occurred while analyzing the website');
    } finally {
      setIsLoading(false);
    }
  };

  const handleReset = () => {
    setResults(null);
    setError(null);
    setAnalyzedUrl('');
  };

  return (
    <div className="App">
      <div className="container py-8">
        {!results ? (
          <>
            <UrlInput onAnalyze={handleAnalyze} isLoading={isLoading} />

            {error && (
              <div className="card border-red-200 bg-red-50">
                <div className="flex items-center gap-3">
                  <span className="text-2xl">❌</span>
                  <div>
                    <h3 className="font-semibold text-red-800">Analysis Failed</h3>
                    <p className="text-red-700">{error}</p>
                    <p className="text-sm text-red-600 mt-2">
                      This might be due to CORS restrictions or the website being unavailable.
                      Try using a different website or check if the URL is correct.
                    </p>
                  </div>
                </div>
              </div>
            )}

            {isLoading && (
              <div className="card text-center">
                <div className="loading">
                  <div className="spinner mr-4"></div>
                  <div>
                    <h3 className="text-lg font-semibold mb-2">Analyzing Website...</h3>
                    <p className="text-gray-600">
                      Fetching and analyzing {analyzedUrl}
                    </p>
                  </div>
                </div>
              </div>
            )}
          </>
        ) : (
          <>
            <div className="flex justify-between items-center mb-6">
              <button
                onClick={handleReset}
                className="btn btn-primary"
              >
                ← Analyze Another Website
              </button>
              <div className="text-sm text-gray-600">
                Last analyzed: {new Date().toLocaleString()}
              </div>
            </div>

            <Results results={results} url={analyzedUrl} />
          </>
        )}

        {/* Footer */}
        <footer className="mt-12 text-center text-gray-500 text-sm">
          <p>
            SEO Metadata Tag Analyzer - A lightweight tool for website SEO optimization
          </p>
          <p className="mt-1">
            Built with React • No data is stored or transmitted
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
