import React, { useState } from 'react';
import UrlInput from './components/UrlInput';
import ManualInput from './components/ManualInput';
import ProxyActivation from './components/ProxyActivation';
import Results from './components/Results';
import { analyzeSEO } from './utils/seoAnalyzer';
import { fetchWebsiteContent, getProxyStatus } from './utils/proxyService';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [analyzedUrl, setAnalyzedUrl] = useState('');
  const [showManualInput, setShowManualInput] = useState(false);
  const [showProxyActivation, setShowProxyActivation] = useState(false);
  const [analysisMethod, setAnalysisMethod] = useState(''); // 'url' or 'manual'

  const handleAnalyze = async (input, isManual = false) => {
    setIsLoading(true);
    setError(null);
    setResults(null);
    setAnalysisMethod(isManual ? 'manual' : 'url');

    if (!isManual) {
      setAnalyzedUrl(input);
    }

    try {
      let htmlContent;

      if (isManual) {
        // Direct HTML analysis
        htmlContent = input;
        setAnalyzedUrl('Manual HTML Input');
      } else {
        // Fetch from URL using proxy services
        htmlContent = await fetchWebsiteContent(input);
      }

      // Analyze the HTML content
      const analysisResults = analyzeSEO(htmlContent);
      setResults(analysisResults);

    } catch (err) {
      console.error('Analysis error:', err);

      // Check if it's a proxy activation issue
      if (err.message.includes('activation') || err.message.includes('403')) {
        setShowProxyActivation(true);
        setError('Proxy services require activation. See instructions below.');
      } else {
        setError(err.message || 'An error occurred while analyzing the content');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleReset = () => {
    setResults(null);
    setError(null);
    setAnalyzedUrl('');
    setShowManualInput(false);
    setShowProxyActivation(false);
    setAnalysisMethod('');
  };

  const handleManualInputToggle = () => {
    setShowManualInput(!showManualInput);
    setError(null);
    setShowProxyActivation(false);
  };

  const handleProxyActivationClose = () => {
    setShowProxyActivation(false);
  };

  // Get current proxy status for debugging
  const proxyStatus = getProxyStatus();

  return (
    <div className="App">
      <div className="container py-8">
        {!results ? (
          <>
            {!showManualInput ? (
              <>
                <UrlInput onAnalyze={handleAnalyze} isLoading={isLoading} />

                {/* Proxy Activation Helper */}
                {showProxyActivation && (
                  <ProxyActivation onClose={handleProxyActivationClose} />
                )}

                {error && !showProxyActivation && (
                  <div className="card border-red-200 bg-red-50">
                    <div className="flex items-center gap-3">
                      <span className="text-2xl">‚ùå</span>
                      <div className="flex-1">
                        <h3 className="font-semibold text-red-800">Analysis Failed</h3>
                        <p className="text-red-700 whitespace-pre-line">{error}</p>
                        <div className="mt-4">
                          <button
                            onClick={handleManualInputToggle}
                            className="btn btn-primary mr-3"
                          >
                            üìù Try Manual HTML Input Instead
                          </button>
                          <button
                            onClick={() => setShowProxyActivation(true)}
                            className="btn bg-blue-600 hover:bg-blue-700 text-white"
                          >
                            üîß Check Proxy Status
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {isLoading && (
                  <div className="card text-center">
                    <div className="loading">
                      <div className="spinner mr-4"></div>
                      <div>
                        <h3 className="text-lg font-semibold mb-2">Analyzing Website...</h3>
                        <p className="text-gray-600">
                          Trying multiple proxy services to fetch {analyzedUrl}
                        </p>
                        <div className="mt-2 text-sm text-gray-500">
                          This may take a few seconds as we try different services...
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {/* Alternative options */}
                <div className="card">
                  <h3 className="text-xl font-semibold mb-4">üîÑ Alternative Analysis Methods</h3>
                  <div className="space-y-3">
                    <div className="p-4 bg-blue-50 rounded-lg">
                      <h4 className="font-semibold text-blue-800 mb-2">üìù Manual HTML Input</h4>
                      <p className="text-blue-700 text-sm mb-3">
                        Paste HTML content directly to bypass CORS restrictions and proxy limitations.
                      </p>
                      <button
                        onClick={handleManualInputToggle}
                        className="btn btn-primary"
                      >
                        Use Manual Input
                      </button>
                    </div>

                    <div className="p-4 bg-green-50 rounded-lg">
                      <h4 className="font-semibold text-green-800 mb-2">üåê Browser Extension</h4>
                      <p className="text-green-700 text-sm mb-3">
                        Install a browser extension to analyze pages directly from your browser.
                      </p>
                      <a
                        href="https://chrome.google.com/webstore/search/seo%20analyzer"
                        target="_blank"
                        rel="noopener noreferrer"
                        className="btn bg-green-600 hover:bg-green-700 text-white"
                      >
                        Find Extensions
                      </a>
                    </div>

                    <div className="p-4 bg-yellow-50 rounded-lg">
                      <h4 className="font-semibold text-yellow-800 mb-2">üîß Proxy Status</h4>
                      <p className="text-yellow-700 text-sm mb-3">
                        Check the status of proxy services and get activation instructions.
                      </p>
                      <button
                        onClick={() => setShowProxyActivation(true)}
                        className="btn bg-yellow-600 hover:bg-yellow-700 text-white"
                      >
                        Check Proxy Status
                      </button>
                    </div>
                  </div>
                </div>
              </>
            ) : (
              <ManualInput 
                onAnalyze={handleAnalyze}
                onBack={handleManualInputToggle}
              />
            )}
          </>
        ) : (
          <>
            <div className="flex justify-between items-center mb-6">
              <button
                onClick={handleReset}
                className="btn btn-primary"
              >
                ‚Üê Analyze Another Website
              </button>
              <div className="text-sm text-gray-600">
                Last analyzed: {new Date().toLocaleString()}
                {analysisMethod === 'manual' && ' (Manual HTML Input)'}
              </div>
            </div>

            <Results results={results} url={analyzedUrl} />
          </>
        )}

        {/* Footer */}
        <footer className="mt-12 text-center text-gray-500 text-sm">
          <p>
            SEO Metadata Tag Analyzer - A lightweight tool for website SEO optimization
          </p>
          <p className="mt-1">
            Built with React ‚Ä¢ No data is stored or transmitted
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
